import { useEffect, useLocation, useState } from 'react';
import "./SinglePost.css";
import singlePostImg from "../img/WAITIEE-Wireless-Charger.jpg";
import axios from "axios";

export default function SinglePost() {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({})

  useEffect(()=>{
    const getPost = async () => {
      const res = await axios.get("/posts/" + path);
      setPost(res.data)
    };
    getPost()
  },[path]);

  return (
    <div className='singlePost'>
        <div className="singlePostWrapper">
          {post.photo && (
            <img src={post.photo} alt="" className="singlePostImg" />
          )}
          <h1 className="singlePostTitle">
            {post.title}
            <div className="singlePostEdit">
            <i className="singlePostIcon fa-solid fa-pen-to-square"></i>
            <i className="singlePostIcon fa-solid fa-trash-can"></i>
            </div>
          </h1>
          <div className="singlePostInfo">
            <span className="singlePostAuthor">Author: <b>Priestly Patrick Bassey</b></span>
            <span className="singlePostDate">{new Date(post.createdAt).toDateString()}</span>
          </div>
          <p className="singlePostDesc">{post.desc}
          </p>
        </div>
    </div>
  )
}
